import pandas as pd
import numpy as np
import matplotlib.pyplot as plt
from scipy.optimize import fmin
from scipy.optimize import minimize
import warnings
warnings.filterwarnings("ignore")

"""# Binary logistic regression with ridge regularization

![cf.JPG](data:image/jpeg;base64,)
"""

class logistic_regression_reg:
    """
    Kelas untuk  Binary logistic regression with ridge regularization
    Kelas ini memiliki beberapa metode yaitu : sigmoid, hypothesis, mapfeature, costfunction, gradient, minimumcost, func, predict, dan score
    """
    def __init__(self):
        pass
    def sigmoid(self,z):
        zt = 1.0/(1+np.exp(-z))
        return zt
    def hypothesis(self,X,theta):
        z = np.dot(X,theta)
        htheta = self.sigmoid(z)
        return htheta
    def mapfeature(self,X,degree):
        data1 = X.copy()
        data1.insert(0,'Ones',1)
        for i in range(1,degree+1):
            for j in range(0,i+1):
                data1['x'+str(i)+str(j)] = np.power(data1[0],i-j)*np.power(data1[1],j)
        cols = [1,2]
        data1.drop(data1.columns[cols],axis = 1,inplace = True)
        return data1
    
    # menghitung cost function 
    def costfunction(self,theta,X,y,lambda_coff):
        m,n = X.shape
        htheta = self.hypothesis(X,theta)
        J = (-1/m) * np.sum(np.multiply(y, np.log(htheta)) + np.multiply((1-y), np.log(1 - htheta)))+ (lambda_coff/(2*m) * sum(theta[1:n] * theta[1:n]))
        return J

    # optimalisasi Cost Function dengan gradien descent 
    def gradient(self,theta,X,y,lambda_coff):
        m,n = X.shape
        htheta =  self.hypothesis(X,theta)
        theta1 = theta[1:n]
        thetaF = np.hstack([0,theta1])
        grad = ((htheta -y)@X)/m + lambda_coff/m*thetaF
        return grad

     # mencari minimum cost function
    def minimumcost(self,costfunction,theta,X,y,lambda_coff):
       min_cost = minimize(fun = costfunction, x0 = theta, args = (X,y,lambda_coff))
       theta_opt = min_cost.x
       return theta_opt
    
    # mengklasifikasi hasil 
    def func(self,x):
        x = np.where(x<0.5,0,1)
        return x

     # memprediksi nilai y
    def predict(self,X,theta):
        ypred = self.hypothesis(X,theta)
        ypred = self.func(ypred)
        return ypred

    # menghitung akurasi
    def score(self,y,ypred):
        Acc = np.mean(y==ypred)
        return Acc